    private void botaoSubtrairActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botaoSubtrairActionPerformed
        // TODO add your handling code here:
        double num1, num2;
        
        num1 = Double.parseDouble(campoPrimeiroNumero.getText());
        num2 = Double.parseDouble(campoSegundoNumero.getText());
        
        if(!campoResultado.getText().equals("")){
            double resultadoAntigo = Double.parseDouble(campoResultado.getText());
            campoResultado.setText(num1 - num2 + resultadoAntigo + "");

        } else {
            campoResultado.setText(num1 - num2 + "");
        }
        
        
    }//GEN-LAST:event_botaoSubtrairActionPerformed

